#!/bin/bash
# HeliosHash DAO Development Aliases

# Project navigation
alias hhdao-cd='cd ~/HeliosHash-DAO'
alias hhdao-web='cd ~/HeliosHash-DAO/apps/web'
alias hhdao-backend='cd ~/HeliosHash-DAO/apps/backend'
alias hhdao-mobile='cd ~/HeliosHash-DAO/apps/mobile'

# Development commands
alias hhdao-dev='pnpm dev'
alias hhdao-build='pnpm build'
alias hhdao-install='pnpm install'

# Help command
alias hhdao-help='echo "HeliosHash DAO Development Aliases:
  hhdao-cd      - Navigate to project root
  hhdao-web     - Navigate to web app
  hhdao-backend - Navigate to backend
  hhdao-mobile  - Navigate to mobile app
  hhdao-dev     - Start development servers
  hhdao-build   - Build project
  hhdao-install - Install dependencies
  hhdao-help    - Show this help"'

echo "ðŸš€ HeliosHash DAO development aliases loaded!"
echo "ðŸ“š Type 'hhdao-help' for available commands"
